import React from "react";
import Link from "next/link";
import { withFormik, Form, Field } from "formik";

const SearchSites = (props) => {
  return (
    <div>
      <div
        className="uk-grid uk-grid-stack uk-grid-small uk-flex-middle uk-child-width-auto@m"
        uk-grid="">
        <div>
          <Link href="/">
            <a className="uk-link-reset uk-text-bold">
              <span className="uk-icon" uk-icon="plus"></span>
              <span>Add Filter</span>
            </a>
          </Link>
        </div>
        <div>
          <Form className="">
            <div
              className="uk-child-width-expand@m uk-width-1-1@m uk-width-medium uk-margin-auto"
              uk-grid={1}>
              <div>
                {/* <div className="uk-inline ">
                  <Link href="/">
                    <a className="uk-link-reset uk-text-bold">
                      <span className="uk-icon" uk-icon="plus"></span>
                      <span>Add Filter</span>
                    </a>
                  </Link>
                </div> */}
                <div className="uk-inline uk-form-controls">
                  <span className="uk-form-icon" uk-icon="icon: search"></span>
                  <Field
                    className="uk-input uk-width-medium@m uk-margin-small-right"
                    name="search"
                    type="text"
                    placeholder="Search sites"
                    style={{ background: "transparent" }}
                    // onInput={(evt)=>searchHandler(evt.target.value)}
                  />
                </div>
                <button className="uk-button uk-button-text uk-text-capitalize uk-text-muted">
                  Cancel
                </button>
              </div>
            </div>
          </Form>
        </div>
      </div>
    </div>
  );
};

const FormikSearchForm = withFormik({
  mapPropsToValues({ search }) {
    return {
      search: search || "",
    };
  },
  handleSubmit(values, { props, resetForm, setErrors, setSubmitting }) {
    resetForm();
    props.searchHandler("");
  },
})(SearchSites);

export default FormikSearchForm;
