import React from "react";
import classes from "./RadioGroup.module.css";
// import { Field } from "formik";

const RadioGroup = ({ children, className = "" }) => {
  return (
    <>
      <div className={[classes.RadioGroup, className, "RadioGroup"].join(" ")}>
        {children}
      </div>
      <style jsx>{`
        .RadioGroup label {
          color: rgb(34, 34, 34);
          display: inline-block;
          cursor: pointer;
          padding: 0.2rem 0.5rem;
        }

        .RadioGroup input[type="radio"]:checked + label {
          color: #ccc8ce;
          background: red;
          transition: background 500ms cubic-bezier(0.77, 0, 0.175, 1);
        }

        .RadioGroup span + span {
          border-left: solid 1px rgb(34, 34, 34);
        }
      `}</style>
    </>
  );
};

export default RadioGroup;

export const RadioInput = (props) => {
  return (
    <span>
      <input type="radio" id={`option-${props.label}`} {...props} />
      <label htmlFor={`option-${props.label}`}>{props.label}</label>
    </span>
  );
};
