import React, { useState } from "react";
import RadioGroup, {
  RadioInput,
} from "@/custom components/RadioGroup/RadioGroup";
import { getRandomNumber } from "utility/helpers";

const LogicRule = ({}) => {
  const [logicOperator, setLogicOperator] = useState("And");

  const [guide, setguide] = useState([]);

  const addGuide = () => {
    if (!guide.length) {
      setguide([
        {
          operator: "contain",
          value: "",
          joiner: "",
        },
      ]);
      return;
    }
    setguide((g) => [
      ...g,
      {
        operator: "not_contain",
        value: "",
        joiner: logicOperator,
      },
    ]);
  };

  const deleteGuide = (idx) => {
    setguide((g) => g.filter((v, i) => idx != i));
  };

  const emptyGuide = () => {
    setguide([]);
  };

  return (
    <div className="uk-card  tm-background-dark">
      {guide.length && (
        <>
          <div className="uk-card-header uk-label-muted card-padding  uk-flex uk-flex-middle uk-flex-between">
            <div className="uk-text-bold">Logic rule</div>
            <div className="font-icon uk-text-middle " onClick={emptyGuide}>
              <span className="fa-stack fa-lg uk-link">
                <i className="fa fa-circle fa-lg fa-stack-1x uk-text-danger "></i>
                <i className="fa fa-trash fa-stack-1x fa-inverse font-18"></i>
              </span>
            </div>
          </div>
          <div className="uk-padding-small">
            {guide.map((val, idx) => {
              return (
                <Guide
                  key={getRandomNumber(5)}
                  deleteHandler={() => deleteGuide(idx)}
                  {...val}
                />
              );
            })}
          </div>
          <div className="uk-text-left uk-margin-small-left uk-margin-small-bottom uk-flex  uk-flex-middle">
            <RadioGroup>
              <RadioInput
                label="&nbsp;And&nbsp;"
                name="operator"
                value="Male"
                checked={logicOperator === "And"}
                onChange={() => setLogicOperator("And")}
              />
              <RadioInput
                label="&nbsp;OR&nbsp;"
                name="operator"
                value="Female"
                checked={logicOperator === "Or"}
                onChange={() => setLogicOperator("Or")}
              />
            </RadioGroup>
            &nbsp;&nbsp;
            <span
              onClick={addGuide}
              className="uk-button uk-button-link uk-text-capitalize tm-text-primary">
              <span className="uk-icon" uk-icon="icon: plus-circle;"></span> Add
              Condition
            </span>
          </div>
        </>
      )}
      <div className="uk-card-footer card-padding uk-label-muted">
        {guide.length ? (
          <span>&nbsp;</span>
        ) : (
          <button
            onClick={addGuide}
            className="uk-button uk-button-small uk-text-capitalize tm-primary uk-text-bold">
            <i className="fa fa-plus "></i>
            Add logic rule
          </button>
        )}
      </div>

      <style jsx>{`
        .card-padding {
          padding: 5px 10px !important;
        }

        .tm-primary {
          background: #1e87f0;
        }

        .tm-text-primary {
          color: #1e87f0;
        }

        .font-icon {
          height: 1em;
          line-height: 1em;
        }

        .font-18 {
          font-size: 18px;
        }

        .font-icon > span {
          height: 1em;
          line-height: 1em;
        }
      `}</style>
    </div>
  );
};

export default LogicRule;

const Guide = ({
  isNumOrDate = false,
  deleteHandler,
  operator = "contain",
  value,
}) => {
  return (
    <div
      className="uk-width-auto uk-grid uk-grid-stack uk-child-width-auto uk-grid-small"
      uk-grid="">
      <div className="">
        <select className="uk-select uk-form-small">
          <option>Div Text</option>
          <option>Option 02</option>
        </select>
      </div>

      <div className="">
        <select className="uk-select uk-form-small" selected={operator}>
          <option value="contain">Contains</option>
          <option value="not_contain">Does not contain</option>
          {isNumOrDate && (
            <>
              <option value="==">Equals</option>
              <option value=">">Greater than</option>
              <option value="<">Less than</option>
              <option value=">=">Greater than/Equals</option>
              <option value="<=">Less than/Equals</option>
            </>
          )}
        </select>
      </div>
      <div>
        <input
          className="uk-input uk-form-small"
          type="text"
          placeholder="value"
          defaultValue={value}
        />
      </div>
      <div>
        <span
          style={{ height: "26px", width: "26px" }}
          onClick={deleteHandler}
          className="uk-icon-link uk-link uk-icon uk-icon-button"
          uk-icon="icon:close;"></span>
      </div>
    </div>
  );
};
