import React from "react";
import { numberInputHandler } from "utility/helpers";
import LogicRule from "./LogicRule";

const Element = ({ type = "number" }) => {
  const onInput = { onInput: type == "number" ? numberInputHandler : null };

  const logic = {
    filter: "",
    rule: [
      {
        operator: "contains",
        value: "",
        join: "",
      },
    ],
  };
  return (
    <>
      <li>
        <a className="uk-accordion-title" href="#">
          Item 1
        </a>
        <a className="uk-accordion-title uk-text-small edit" href="#">
          Edit Filter
        </a>
        <div className="info">dsfs</div>

        <div className="uk-accordion-content" hidden="1">
          <div
            className="uk-grid-small uk-grid uk-grid-stack uk-child-width-1-2@m"
            uk-grid="">
            <div>
              <label className="uk-form-label" htmlFor="filterTrigger">
                Filter Trigger*
              </label>

              <select className="uk-select" id="filterTrigger">
                <option>CMS Collection List</option>
                <option>Static Div, Button, Link</option>
                <option>Select</option>
              </select>
            </div>
            <div>
              <label className="uk-form-label" htmlFor="filterBy">
                filter-by*
              </label>
              <input
                className="uk-input"
                id="filterBy"
                type="text"
                name="filterBy"
                {...onInput}
              />
            </div>
          </div>
          <div className="uk-margin-small-top">
            <LogicRule />
          </div>
          <div className="uk-margin-small-top">
            <button className="uk-button uk-button-danger uk-button-small uk-text-capitalize uk-float-right">
              Delete element
            </button>
          </div>
        </div>
      </li>
      <style jsx>{`
        li.uk-open > .uk-accordion-title:not(.edit) {
          display: none;
        }

        li.uk-open > .info {
          display: none;
        }

        li:not(.uk-open) > .edit {
          display: none;
        }
      `}</style>
    </>
  );
};

export default Element;
